              const show = isHovered || isSelected;
              const fill = isHovered ? HOVER_FILL : isSelected ? SELECT_FILL : "rgba(0,0,0,0)";
              const stroke = isHovered ? HOVER_STROKE : isSelected ? SELECT_STROKE : "rgba(0,0,0,0)";
